217;;//H3見出しのパターン<br />\n    if ( preg_match( $h3, $the_content, $h3s )) {//H3見出しが本文中にあるかどうか<br />\n      $the_content  = preg_replace($h3, $ads.$h3s[0], $the_content, 1);//最初のH3を置換<br />\n    }<br />\n  }<br />\n  return $the_content;<br />\n}<br />\nadd_filter(&#8216;the_content&#8217;,&#8217;add_ads_before_1st_h3&#8242;);</p>\n<p>&#8212;&#8212;&#8212;&#8212;&#8212;&#8212;&#8212;&#8212;&#8212;&#8212;&#8212;&#8212;&#8212;&#8212;&#8212;&#8212;&#8211;</p>\n						\n					\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}s:32:\"http://purl.org/dc/elements/1.1/\";a:1:{s:7:\"creator\";a:1:{i:0;a:5:{s:4:\"data\";s:5:\"boon1\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}}}}i:7;a:6:{s:4:\"data\";s:55:\"\n					\n					\n					\n					\n					\n\n					\n\n					\n					\n				\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";s:5:\"child\";a:2:{s:0:\"\";a:5:{s:4:\"guid\";a:1:{i:0;a:5:{s:4:\"data\";s:247:\"https://ja.wordpress.org/support/topic/hueman%e3%80%80%e8%8b%b1%e8%aa%9e%e8%a1%a8%e8%a8%98%e3%81%8c%e6%97%a5%e6%9c%ac%e8%aa%9e%e8%a1%a8%e8%a8%98%e3%81%ab%e5%a4%89%e3%82%8f%e3%81%a3%e3%81%a6%e3%81%97%e3%81%be%e3%81%84%e3%81%be%e3%81%97/#post-213876\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:5:\"title\";a:1:{i:0;a:5:{s:4:\"data\";s:128:\"返信先: hueman　英語表記が日本語表記に変わってしまいました。戻す方法はありますでしょうか\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"xml_base_explicit\";b:0;s:8:\"xml_lang\";s:0:\"\";}}s:4:\"link\";a:1:{i:0;a:5:{s:4:\"data\";s:247:\"https://ja.wordpress.org/support/topic/hueman%e3%80%80%e8%8b%b1%e8%aa%9e%e8%a1%a8%e8%a8%98%e3%81%8c%e6%97%a5%e6%9c%ac%e8%aa%9e%e8%a1%a8%e8%a8%98%e3%81%ab%e5%a4%89%e3%82%8f%e3%81%a3%e3%81%a6%e3%81%97%e3%81%be%e3%81%84%e3%81%be%e3%81%97/#post-213876\";s:7:\"attribs\";a:0:{}s:8:\"xml_base\";s:0:\"\";s:17:\"x